sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Assign script specific variables to capture details

	var sum = 0;

	// Capture PA Suggessted Resources to an array
		var pauserArray = [];

	// Assign PA values to the array
		pauserArray = current.pa_sugesstions.split(",");

	// Capture Array values in logs to check for any errors
	gs.info ("The PA array values which I have got are:" +pauserArray);

	// Make a gliderecord call to PA allocation table to start iterating over an array and sum up the hrs
	var paallocation = new GlideRecord('x_snc_customer_out_pa_allocations');

	// Iterating over the array and then summing up values
	for (i=0; i<pauserArray.length; i++) {
	var paname = pauserArray[i];
	gs.info(("The value in the loop is:" + paname));	  
	if (paname) {
	var querystring = 'name=' +paname;
	paallocation.addQuery();
	gs.info("The value in query string is:" + querystring);
	sum =+ paallocation.total_hours;
	gs.info(("The total hrs for PA resource is:" + sum));
	}	
	}

})(current, previous);
